var link=document.querySelector(".feedback-btn"),popup=document.querySelector(".feedback-popup"),overlay=document.querySelector(".overlay"),close=popup.querySelector(".feedback-close-btn");link.addEventListener("click",function(a){a.preventDefault(),popup.classList.add("popup-show"),overlay.classList.add("overlay-show")}),close.addEventListener("click",function(a){a.preventDefault(),popup.classList.remove("popup-show"),overlay.classList.remove("overlay-show")}),window.addEventListener("keydown",function(a){27===a.keyCode&&popup.classList.contains("popup-show")&&(popup.classList.remove("popup-show"),overlay.classList.remove("overlay-show"))});